var USELESS_IMAGE = /icon|logo|avatar|small|banner|promo|author|placeholder|button|track|null/i;
var WIDTH_TIMES_HEIGHT = /[^0-9](0|[1-9][0-9]*)(?:x|\*)(0|[1-9][0-9]*)/g;
