var metaDescription = domHelpers.buildAttributeMatcher({
  property: ['rnews:description', 'og:description', 'twitter:description'],
  name: ['description', 'lp', 'twitter:description'],
  itemprop: ['description']
});

TextAlgoritme.prototype.element = function (node) {
  // Grap the <meta> tags, there may contain some valuable information.
  if (node.tagname === 'meta' && metaDescription(node.attr) && node.attr.content) {
    this._handleMeta(domHelpers.normalizeString(node.attr.content));
  }
  // Grap actual text containing elements
  // NOTE: out if 258 html files the lowerst density in the 40 most dense
  // elements was 12, so for optimization don't add elements with a density
  // lower than 10. Something lower than 10 would mostlikely also be useless.
  else if (node.density > 10) {
    this._handleText(node);
  }
};

TextAlgoritme.prototype._handleMeta = function (text) {
  text = text.trim();

  // Check if this is a new string, in case of dublication there is no need
  // for calculating the distances again, just incease a counter
  var index = this._metaText.indexOf(text);

  if (index === -1) {
    // Not found add a new string
    this._metaText.push(text);

    // Create and store meta object
    this._meta.push(new TextMetaItem(text));

    // TODO: use the same pattern as in title.js
  } else {
    // The text already exists, just incease the appear counter
    this._meta[index].increaseAppear();
  }
};

TextAlgoritme.prototype._handleText = function (node) {
  this._elements.push(node);
};

TextAlgoritme.prototype._analysePair = function (elemA, elemB) {
  // Match every elements against evey other element and find there
  // common parent, this includes the element itself.
  var parentNode = domHelpers.commonParent(elemA, elemB);

  // The root, html and body are not valid parents, since they will always
  // contain too much data
  if (parentNode.type === 'root' || parentNode.tagname === 'html' || parentNode.tagname === 'body') {
    return;
  }

  // Increase the counter if the parentNode has already been analysed
  if (this._containerMap.hasOwnProperty(parentNode.identifyer) === true) {
    this._containerMap[parentNode.identifyer].increaseCount();
    return;
  }

  // The size is text crazy big, even if it is correct it will take a long
  // time to process
  var rawText = parentNode.getText().trim();
  if (rawText.length > 20000) return;

  // If another node contains the same text, don't create a new parent
  // just increase its counter
  var identifyer = this._containerText[rawText];
  if (identifyer !== undefined) {
    this._containerMap[identifyer].increaseCount();
    return;
  }

  // Create the container object and maintain the keys array
  this._containerText[rawText] = parentNode.identifyer;
  this._containerMap[parentNode.identifyer] = new TextContainerItem(this, parentNode, rawText);
};

// The end of the document is reached
TextAlgoritme.prototype.end = function () {
  // Take the 40 best elements mesured by the density
  var elements = this._elements
    .sort(function (a, b) {
      return b.density - a.density;
    })
    .slice(0, 40);

  // Do a x/x table match to find the common parents
  var l = elements.length;
  for (var i = 0; i < l; i++) {
    for (var j = i; j < l; j++) {
      this._analysePair(elements[i], elements[j]);
    }
  }

  // Calculate the likelihood for each commonParent
  var commonParent = [];
  var keys = Object.keys(this._containerMap);
  for (var n = 0, t = keys.length; n < t; n++) {
    var item = this._containerMap[ keys[n] ];
        item.calculateLikelihood();
    commonParent.push(item);
  }

  // Now sort the remaining commonParent containers by likelihood and then
  // their position
  this._containers = commonParent.sort(function (a, b) {
    var likelihood = b.likelihood - a.likelihood;
    if (likelihood !== 0) return likelihood;
    return b.node.identifyer - a.node.identifyer;
  });
};

TextAlgoritme.prototype.result = function () {
  return this._containers;
};

var mathHelpers = require('../helpers-math.js');

var WORD_SPLIT = /\s+/;
var LINE_SPLIT = /\n+/;
var CONTAIN_SENTENCE = /\.[^a-zA-Z]+(?=[A-Z]|$)/;

function TextContainerItem(algoritme, node, text) {
  this._algoritme = algoritme;

  this.node = node;

  // Diffrent text properties
  this.text = text;
  this.lines = text.split(LINE_SPLIT);
  this.words = text.split(WORD_SPLIT);

  // A linear appear value
  this._count = 0;
  this.increaseCount();

  // The scores
  this._shortline = 0;
  this._linebreaks = 0;
  this._classcount = 0;
  this._styleratio = 0;
  this._paragraphs = 0;
  this._density = 0;
  this._match = [];

  // Calculate scores
  this._calculateScores();
  for (var i = 0, l = algoritme._meta.length; i < l; i++) {
    this.appendDistance(algoritme._meta[i]);
  }
}
module.exports = TextContainerItem;

TextContainerItem.prototype.increaseCount = function () {
  var algoritme = this._algoritme;

  this._count += 1;
  algoritme._adjustContainerCount.update(this._count);
};

TextContainerItem.prototype._calculateScores = function () {
  this._calculateShortline();
  this._calculateLinebreaks();
  this._calculateClasscount();
  this._calculateStyleratio();
  this._calculateSentenses();
  this._calculateParagraphs();
  this._calculateDensity();
};

// Containers with short lines should be punished
TextContainerItem.prototype._calculateShortline = function () {
  var algoritme = this._algoritme;

  // Count the amount of short lines (40 chars)
  var shortlines = 0;
  for (var a = 0, m = this.lines.length; a < m; a++) {
    if (this.lines[a].length <= 40) shortlines += 1;
  }

  // Update property
  this._shortlines = shortlines;
  algoritme._adjustShortline.update(shortlines);
};

// This function is made by the following requirements:
// x > 5, y = 1
// f(x) = a*sqrt(x) + b*x^2 + c
// f(0) = 0.2
// f(5) = 1
// f'(5) = 0
function linebreakScore(x) {
  if (x > 5) return 1;
  else return Math.min(1, 0.477 * Math.sqrt(x) - 0.0106 * Math.pow(x, 2) + 0.2);
}

// Containers with very few lines should be punished
TextContainerItem.prototype._calculateLinebreaks = function () {
  var algoritme = this._algoritme;
  var linebreaks = linebreakScore(this.lines.length);

  // Update property
  this._linebreaks = linebreaks;
  algoritme._adjustLinebreak.update(linebreaks);
};

var CLASS_COUNT_SCORES = [1, 0.85, 0.73, 0.63, 0.54, 0.44, 0.34, 0.26, 0.22, 0.2];
function classcountScore(x) {
  if (x <= 1) return 1;
  else if (x >= 10) return 0.2;
  else return CLASS_COUNT_SCORES[x - 1];
}

// Containers classes there aren't wildly used is prefered
TextContainerItem.prototype._calculateClasscount = function () {
  var algoritme = this._algoritme;
  var classlist = this.node.root.classlist;

  // Calculcate a classcount score
  var max = 0, count = 0;
  for (var i = 0, l = this.node.classes.length; i < l; i++) {
    count = classlist.count(this.node.classes[i]);
    if (max < count) max = count;
  }
  var classcount = classcountScore(max);

  // Update property
  this._classcount = classcount;
  algoritme._adjustClasscount.update(classcount);
};

// Compare the amount of styled text with the total text length
// E.q. this will prevent long lists of links to be registred as a
// text container
TextContainerItem.prototype._calculateStyleratio = function () {
  var algoritme = this._algoritme;
  var noneStyledText = this.node.getNoneStyledText().trim();
  var styleratio = 1 - (this.text.length - noneStyledText.length) / this.text.length;

  // Update property
  this._styleratio = styleratio;
  algoritme._adjustStyleRatio.update(styleratio);
};

// Count the content of the 2 bigest groupes of paragraphs
TextContainerItem.prototype._calculateSentenses = function () {
  var algoritme = this._algoritme;

  // Count the amount of sentenses containing paragraphs in a row.
  var collection = [0];
  var paragraphSize = 0;
  for (var i = 0, l = this.lines.length; i < l; i++) {
    if (CONTAIN_SENTENCE.test(this.lines[i]) === false) {
      // found bad paragraph push and reset counter
      if (paragraphSize !== 0) {
        collection.push(paragraphSize);
        paragraphSize = 0;
      }
    } else {
      paragraphSize += this.lines[i].length;
    }
  }
  if (paragraphSize !== 0) collection.push(paragraphSize);

  // Find the two largest paragraph collections
  var maxes = [0, 0];
  for (var a = 0, r = collection.length; a < r; a++) {
    if (collection[a] >= maxes[1]) {
      maxes[0] = maxes[1];
      maxes[1] = collection[a];
    }
  }

  // Calculate the total text length
  var sentenses = maxes[0] + maxes[1];

  // Update property
  this._sentenses = sentenses;
  algoritme._adjustSentensesCount.update(sentenses);
};

// Simply count the amount of <p> tags
TextContainerItem.prototype._calculateParagraphs = function () {
  var algoritme = this._algoritme;
  var paragraphs = this.node.countTagname('p');

  // Update property
  this._paragraphs = paragraphs;
  algoritme._adjustParagraphCount.update(paragraphs);
};

// Use the parent density as a feature
TextContainerItem.prototype._calculateDensity = function () {
  var algoritme = this._algoritme;
  var density = this.node.density;

  // Update property
  this._density = density;
  algoritme._adjustContainerDensity.update(density);
};

// Calculate meta comparison scores
TextContainerItem.prototype.appendDistance = function (meta) {
  this._match.push(meta.calculateMatch(this));
};

// This function is made by the following requirements:
// : x < 0.01 , y = 0.5
// : x > 0.1 , y = 1
// : f(x), otherwise
// The f(x) polynomium is made so it follows:
// : f(0.01) = 0.5
// : f'(0.01) = 0
// : f(0.1) = 1
// : f'(0.1) = 0
function adjustLiklihood(x) {
  if (x < 0.01) return 0.5;
  else if (x > 0.1) return 1;
  else return Math.min(1, - 1371 * Math.pow(x, 3) + 226 * Math.pow(x, 2) - 4.11 * x + 0.52);
}

TextContainerItem.prototype.calculateLikelihood = function () {
  var algoritme = this._algoritme;
  var meta = algoritme._meta;

  // Adjust the single value scores
  var count = algoritme._adjustContainerCount.adjust(this._count);
  var shortline = 1 - algoritme._adjustShortline.adjust(this._shortlines);
  var linebreaks = algoritme._adjustLinebreak.adjust(this._linebreaks);
  var classcount = algoritme._adjustClasscount.adjust(this._classcount);
  var styleratio = algoritme._adjustStyleRatio.adjust(this._styleratio);
  var sentenses = algoritme._adjustSentensesCount.adjust(this._sentenses);
  var paragraphs = algoritme._adjustParagraphCount.adjust(this._paragraphs);
  var density = algoritme._adjustContainerDensity.adjust(this._density);

  // Use the descriptions to calculate a match feature if possible
  var match = 1;
  if (meta.length > 0) {
    var total = 0, divider = 0;
    for (var i = 0, l = meta.length; i < l; i++) {
      total += meta[i]._match.appearAdjust(meta[i].appear, this._match[i]);
      divider += meta[i].appear;
    }
    match = total / divider;
  }

  // Calculate the likelihood
  var scores = [
    count, shortline, linebreaks, classcount, styleratio,
    sentenses, paragraphs, density, match
  ];
  var likelihood = mathHelpers.distance(scores);
  var minimum = Math.min.apply(Math, scores);

  // Some containers may win on adjused features like (1,0) under the right
  // conditions. The goal of this transformation of likelihood is to make
  // those elements unlikly, however not to the point where removing them
  // would be the same.
  // SEE: 26c3b98f33bb6902f32535235fd7d32792df87779bdf1f86c3be15fbf3161d
  this.likelihood = adjustLiklihood(minimum) * likelihood;
};

var fusspos = require('fusspos');

var mathHelpers = require('../helpers-math.js');

var WORD_SPLIT = /\s+/;

function TextMetaItem(text) {
  this.appear = 1;

  this.text = text;
  this.words = text.split(WORD_SPLIT);

  this._match = new mathHelpers.RangeObject();
}
module.exports = TextMetaItem;

TextMetaItem.prototype.increaseAppear = function () {
  this.appear += 1;
};

TextMetaItem.prototype.calculateMatch = function (item) {
  var search = fusspos(this.words, item.words);

  // Calculate the probability for the description
  var probability = 0;
  if (Number.isNaN(search.middle) === false) {
    probability = ((item.words.length - search.start) / item.words.length) * (1 - search.fussy);
  }

  // Update range object
  this._match.update(probability);
  return probability;
};

var url = require('url');

var domHelpers = require('../helpers-dom.js');
var mathHelpers = require('../helpers-math.js');

var ImageItem = require('./image-item.js');

function ImageAlgoritme(main) {
  this._imageMap = {};

  this._images = [];

  this._twitter = {
    card: null,
    node: null,
    href: null
  };

  this._base = main._source;

  this._adjustSize = new mathHelpers.RangeObject();
  this._adjustMetaCount = new mathHelpers.RangeObject();
  this._adjustBodyCount = new mathHelpers.RangeObject();
}
module.exports = ImageAlgoritme;

var metaOpenGraph = domHelpers.buildAttributeMatcher({
  property: ['og:image']
});

var metaTwitter = domHelpers.buildAttributeMatcher({
  name: ['twitter:image', 'twitter:card']
});

ImageAlgoritme.prototype.element = function (node) {
  // Grap the <meta> tags, a link is a link so meta urls are good sugestions
  if (node.tagname === 'meta' && metaOpenGraph(node.attr) && node.attr.content) {
    this._handleOpenGraph(node);
  }
  else if (node.tagname === 'meta' && metaTwitter(node.attr)) {
    this._handleTwitter(node);
  }
  // Grap <img> tags
  else if (node.tagname === 'img' && node.attr.src) {
    this._handleImage(node);
  }
  // Grap <object> tags
  else if (node.tagname === 'object') {
    this._handleObject(node);
  }
};

ImageAlgoritme.prototype._appendImage = function (hrefSource, size, type, node) {
  var collection;

  // decode HTML entities
  var href = domHelpers.decodeEntities(domHelpers.decodeEntities(hrefSource));

  // Resolve relative hrefs
  if (href.slice(0, 7) !== 'http://' && href.slice(0, 8) !== 'https://') {
    href = url.resolve(this._base, href);
  }

  if (this._imageMap.hasOwnProperty(href) === false) {
    collection = this._imageMap[href] = new ImageItem(this, href);
  } else {
    collection = this._imageMap[href];
  }

  if (size) collection.setSize(size);
  collection.addNode(type, node);
};

//
// Node handlers
//
ImageAlgoritme.prototype._handleOpenGraph = function (meta) {
  this._appendImage(meta.attr.content, null, 'meta:og', meta, true);
};

// These are estimates on the twitter image size depending on the card type
var TWITTER_SIZE = {
  'summary': { width: 120, height: 120 },
  'summary_large_image': { width: 280, height: 150 },
  'photo': { width: 280, height: 150 },
  'player': { width: 350, height: 196 },
  'product': { width: 160, height: 160 }
};

ImageAlgoritme.prototype._handleTwitter = function (meta) {
  var content = meta.attr.hasOwnProperty('value') ?
        meta.attr.value : meta.attr.content;

  // Store meta information
  if (meta.attr.name === 'twitter:image') {
    this._twitter.node = meta;
    this._twitter.href = content;
  } else if (meta.attr.name === 'twitter:card') {
    this._twitter.card = content.toLowerCase();
  }

  // When enogth information is gained append the image
  if (this._twitter.href !== null &&
      this._twitter.card !== null &&
      TWITTER_SIZE.hasOwnProperty(this._twitter.card)) {

    var cardSize = TWITTER_SIZE[this._twitter.card];
    this._appendImage(
      this._twitter.href,
      new mathHelpers.ImageSize(cardSize.width, cardSize.height),
      'meta:twitter',
      this._twitter.node
    );
  }
};

ImageAlgoritme.prototype._handleImage = function (img) {
  var style = domHelpers.styleParser(img.attr.style);
  var size = new mathHelpers.ImageSize(
    img.attr.width || style.width,
    img.attr.height || style.height
  );

  this._appendImage(img.attr.src, size, 'img', img);
};

ImageAlgoritme.prototype._handleObject = function (node) {
  // Create param map
  var params = {};
  for (var i = 0, l = node.children.length; i < l; i++) {
    if (node.children[i].tagname === 'param') {
      params[node.children[i].attr.name] = node.children[i].attr.value;
    }
  }

  // if a holdingImage property exists then append the image
  if (params.hasOwnProperty('holdingImage')) {
    var size = new mathHelpers.ImageSize(params.width, params.height);

    this._appendImage(params.holdingImage, size, 'media', node);
  }
};

// The end of the document is reached
ImageAlgoritme.prototype.end = function () {
  var keys = Object.keys(this._imageMap);

  // Inform the image object that no more nodes can be added
  // and skip the image if it turned out to be useless
  for (var i = 0, l = keys.length; i < l; i++) {
    var imageObject = this._imageMap[keys[i]];
        imageObject.done();

    // Skip useless images
    if (imageObject.useless === false) {
      this._images.push(imageObject);
    }
  }

  // Calculcate the likelihood for each remainging image
  for (var c = 0, t = this._images.length; c < t; c++) {
    var imageObject = this._images[c];
        imageObject.calculateLikelihood();
  }

  // Sort the images by the calculated likelihood
  this._images = this._images.sort(function (a, b) {
    return b.likelihood - a.likelihood;
  });
};

ImageAlgoritme.prototype.result = function () {
  return this._images;
};

var url = require('url');
var path = require('path');

var mathHelpers = require('../helpers-math.js');
var domHelpers = require('../helpers-dom.js');

var GOOD_IMAGE = /jumbo|large/i;
var USELESS_IMAGE = /icon|logo|avatar|small|banner|promo|author|placeholder|button|track|null/i;
var WIDTH_TIMES_HEIGHT = /[^0-9](0|[1-9][0-9]*)(?:x|\*)(0|[1-9][0-9]*)/g;
var PREFIX_WIDTH = /w([1-9][0-9]*)/;

function ImageItem(algoritme, href) {
  this._algoritme = algoritme;

  this.from = [];
  this.href = href;
  this.likelihood = 0;

  this._count = {
    'body': 0,
    'meta': 0
  };

  this._types = {
    'meta:og' : 0,
    'meta:twitter': 0,
    'img': 0,
    'media': 0
  };

  this._parsed = url.parse(href, true);
  this._extname = path.extname(this._parsed.pathname).toLowerCase() || null;

  // Set some properies by analysing the url
  this.useless = this._uselessFromUrl();
  this._size = this._sizeFromUrl();
